import android.content.Intent; 

import com.msi.androidrss.ShowDescription; 

public class RSSReader extends Activity implements OnItemClickListener 
{ 

    public final String RSSFEEDOFCHOICE = "http://www.ibm.com/developerworks/views/rss/customrssatom.jsp?zone_by=XML ;amp;zone_by=Java ;amp;zone_by=Rational ;amp;zone_by=Linux ;amp;zone_by=Open+source ;amp;zone_by=WebSphere ;amp;type_by=Tutorials ;amp;search_by= ;amp;day=1 ;amp;month=06 ;amp;year=2007 ;amp;max_entries=20 ;amp;feed_by=rss ;amp;isGUI=true ;amp;Submit.x=48 ;amp;Submit.y=14"; 

    private static final int SELECT = 0; 
    private static final int REFRESH = 1; 

    public final String tag = "RSSReader"; 
    private RSSFeed feed = null; 

    /** Called when the activity is first created. */ 

    public void onCreate(Bundle icicle) { 
        super.onCreate(icicle); 
        setContentView(R.layout.main); 

        // go get our feed! 
        feed = getFeed(RSSFEEDOFCHOICE); 

        // display UI 
        UpdateDisplay(); 

    } 


    private RSSFeed getFeed(String urlToRssFeed) 
    { 
        try 
        { 
            // setup the url 
           URL url = new URL(urlToRssFeed); 

           // create the factory 
           SAXParserFactory factory = SAXParserFactory.newInstance(); 
           // create a parser 
           SAXParser parser = factory.newSAXParser(); 

           // create the reader (scanner) 
           XMLReader xmlreader = parser.getXMLReader(); 
           // instantiate our handler 
           RSSHandler theRssHandler = new RSSHandler(); 
           // assign our handler 
           xmlreader.setContentHandler(theRssHandler); 
           // get our data via the url class 
           InputSource is = new InputSource(url.openStream()); 
           // perform the synchronous parse            
           xmlreader.parse(is); 
           // get the results - should be a fully populated RSSFeed instance, or null on error 
           return theRssHandler.getFeed(); 
        } 
        catch (Exception ee) 
        { 
            // if we have a problem, simply return null 
            return null; 
        } 
    } 
    public boolean onCreateOptionsMenu(Menu menu)  
    { 
        super.onCreateOptionsMenu(menu); 
     menu.add(0, RSSReader.SELECT, 0, "Choose RSS Feed").setIcon(android.R.drawable.ic_menu_mapmode); 
        //menu.add(0,0,"Choose RSS Feed"); 
        //menu.add(0,1,"Refresh"); 
     menu.add(0, RSSReader.REFRESH, 0, "Refresh").setIcon(android.R.drawable.ic_menu_mapmode); 
        Log.i(tag,"onCreateOptionsMenu"); 
        return true; 
    } 

    @Override 
    public boolean onMenuItemSelected(final int featureId, final MenuItem item) { 
        switch (item.getItemId()) { 
        case RSSReader.SELECT: 

            Log.i(tag,"Set RSS Feed"); 
            return true; 
        case RSSReader.REFRESH: 
            Log.i(tag,"Refreshing RSS Feed"); 
            return true; 
        } 
        return false; 
    } 


    private void UpdateDisplay() 
    { 
        TextView feedtitle = (TextView) findViewById(R.id.feedtitle); 
        TextView feedpubdate = (TextView) findViewById(R.id.feedpubdate); 
        ListView itemlist = (ListView) findViewById(R.id.itemlist); 


        if (feed == null) 
        { 
            feedtitle.setText("No RSS Feed Available"); 
            return; 
        } 

        feedtitle.setText(feed.getTitle()); 
        feedpubdate.setText(feed.getPubDate()); 

        ArrayAdapter  RSSItem  adapter = new ArrayAdapter  RSSItem (this,android.R.layout.simple_list_item_1,feed.getAllItems()); 

        itemlist.setAdapter(adapter); 

        itemlist.setOnItemClickListener(this); 

        itemlist.setSelection(0); 

    } 




    public void onItemClick(AdapterView  ?  parent, View v, int position, long id) 

     { 
         Log.i(tag,"item clicked! [" + feed.getItem(position).getTitle() + "]"); 

         Intent itemintent = new Intent(this,ShowDescription.class); 

         Bundle b = new Bundle(); 
         b.putString("title", feed.getItem(position).getTitle()); 
         b.putString("description", feed.getItem(position).getDescription()); 
         b.putString("link", feed.getItem(position).getLink()); 
         b.putString("pubdate", feed.getItem(position).getPubDate()); 

         itemintent.putExtra("android.intent.extra.INTENT", b); 

         startActivity(itemintent); 
     } 

} 
