protected void notify(EventNotifier notifier, ConnectionEvent event) { 
    assert (event != null); 
    Collection  ConnectionEventListener  workingCopy = null; 
    synchronized (_listeners) { 
        workingCopy = new ArrayList  ConnectionEventListener (_listeners); 
    } 
    for (ConnectionEventListener listener : workingCopy) { 
