 p The PS URL is simply a curl request function that responds with 404, 200, 500 etc which is then used to populate the relevant cell.    

 pre  code function getPage( $url ) { 
$options = array( 
    CURLOPT_URL             =  $url, 
    CURLOPT_RETURNTRANSFER =  true,                 // return web page 
    CURLOPT_HEADER         =  true,                 // return headers 
    CURLOPT_FOLLOWLOCATION =  true,                 // follow redirects 
    CURLOPT_ENCODING       =  "",                   // handle all encodings 
    CURLOPT_USERAGENT      =  "pre-surf",           // who am i 
    CURLOPT_AUTOREFERER    =  true,                 // set referer on redirect 
    CURLOPT_CONNECTTIMEOUT =  120,                  // timeout on connect 
    CURLOPT_TIMEOUT        =  120,                  // timeout on response 
    CURLOPT_MAXREDIRS      =  10,                   // stop after 10 redirects 
    CURLOPT_POST            =  0,                   // i am not sending post data 
    CURLOPT_SSL_VERIFYHOST =  0,                    // don't verify ssl 
    CURLOPT_SSL_VERIFYPEER =  FALSE,                // 
); 

$ch      = curl_init(); 
curl_setopt_array($ch, $options); 
$content = curl_exec($ch); 
$err     = curl_errno($ch); 
$errmsg  = curl_error($ch) ; 
$header  = curl_getinfo($ch); 
curl_close($ch); 

//  $header['errno']   = $err; 
//  $header['errmsg']  = $errmsg; 
//  $header['content'] = $content; 
return $header['http_status_code']; 
} 
