 p You can provide a specific implementation of ResourceBundle.    

 p Here an example that gets the current locale each time JSF invokes the ResourceBundle methods:    

 pre  code package my.company.jsf.util; 

import java.util.Enumeration; 
import java.util.HashMap; 
import java.util.Iterator; 
import java.util.Locale; 
import java.util.Map; 
import java.util.ResourceBundle; 

import javax.faces.context.FacesContext; 

public class MyBundle extends ResourceBundle { 

    private static final Map  Locale, ResourceBundle  RB_CACHE = new HashMap  Locale, ResourceBundle (); 
    private static final String BUNDLE_NAME = "my-messages"; 

    public MyBundle() { 
    } 

    @Override 
    public Enumeration  String  getKeys() { 
        ResourceBundle rb = getResourceBundle(); 
        final Iterator  String  it = rb.keySet().iterator(); 
        return new Enumeration  String () { 

            @Override 
            public boolean hasMoreElements() { 
                return it.hasNext(); 
            } 

            @Override 
            public String nextElement() { 
                return it.next(); 
            } 

        }; 
    } 


    @Override 
    protected Object handleGetObject(String key) { 
        ResourceBundle rb = getResourceBundle(); 
        return rb.getObject(key); 
    } 

    private ResourceBundle getResourceBundle() { 
        Locale locale = FacesContext.getCurrentInstance().getViewRoot().getLocale(); 
        ResourceBundle rb = RB_CACHE.get(locale); 
        if (rb == null) { 
            rb = ResourceBundle.getBundle(BUNDLE_NAME, locale); 
            RB_CACHE.put(locale, rb); 
        } 
        return rb; 
    } 
} 
